/* Invert the graphics screen */void InvertScreen(void)/* Invert all of the bits on the graphics screen */{int *ptr;                               /* graphics screen pointer */int count;                              /* loop counter */ptr = (int *) 0xE12000;for (count = 0; count < 16000; ++count) {   *ptr = ~*ptr;   ++ptr;   }}void main(void){int loop;InvertScreen();for (loop = 1; loop <= 100; ++loop)   ;InvertScreen();}