/* Start the text tools for    *//* a stand-alone text program. */void StartText (void){TextStartUp();SetErrGlobals(127,0);SetErrorDevice(1,3L);SetInGlobals(127,0);SetInputDevice(1,3L);SetOutGlobals(127,0);SetOutputDevice(1,3L);}/* Shut down the text tools    *//* for a stand-alone text      *//* program.                    */void EndText (void){TextShutDown();}