#include <stdio.h>#include <string.h>void main(void){printf("strcmp returns %d if the first string is less than the second.\n",   strcmp("apple", "orange"));printf("strcmp returns %d if the first string is greater than the second.\n",   strcmp("orange", "apple"));printf("strncmp returns %d if the first string is less than the second.\n",   strncmp("apple", "orange", 10));printf("strncmp returns %d if the first string is greater than the second.\n",   strncmp("orange", "apple", 10));}