/* Demonstrates that leaving out the break statement    *//* can occasionally result in shorter programs.         */#include <stdio.h>void main (void){unsigned i;for (i = 1; i <= 5; ++i) {   switch (i) {      case 5: printf("*");      case 4: printf("*");      case 3: printf("*");      case 2: printf("*");      case 1: printf("*");      }   printf("\n");   }}