/* Test strcmp2 */#include <ctype.h>#include <stdio.h>#include <string.h>int strcmp2 (char *p1, char *p2){while (*p1 && *p2)   {   if (toupper(*p1) !=       toupper(*p2)) {      if (toupper(*p1) <          toupper(*p2))         return -1;      return 1;      }   ++p1;   ++p2;   }if (*p1)   return 1;if (*p2)   return -1;return 0;}void main (void){char string1[20], *cp;strcpy(string1, "HO");cp = string1;printf("%d %d\n",  1, strcmp2("ho", "HI"));printf("%d %d\n", -1, strcmp2("hi", string1));printf("%d %d\n",  0, strcmp2("ho", cp));printf("%d %d\n",  1, strcmp2("hoh", "HO"));printf("%d %d\n", -1, strcmp2("ho", "HOH"));}