#include <stdio.h>#define TRUE 1#define FALSE 0#define BOOL unsignedint a[10];void ShellSort (void){BOOL done;int i, t;do {   done = TRUE;   for (i = 0; i < 9; ++i)      if (a[i] > a[i+1]) {         t = a[i];         a[i] = a[i+1];         a[i+1] = t;         done = FALSE;         }   }while (!done);}void BubbleSort (void){int i, j, t;for (i = 1; i < 10; ++i)   if (a[i] < a[i-1]) {      j = i;      while ((j > 0) && (a[j] < a[j-1])) {         t = a[j];         a[j] = a[j-1];         a[j-1] = t;         --j;         }      }}int main (void){unsigned i;for (i = 0; i < 10; ++i)  a[i] = 20-i;ShellSort();for (i = 0; i < 10; ++i)  printf("%8d %8d\n", i, a[i]);printf("\n");for (i = 0; i < 10; ++i)  a[i] = 20-i;BubbleSort();for (i = 0; i < 10; ++i)  printf("%8d %8d\n", i, a[i]);}